function [fmseis,line_name,ntr,nrec,xs,ys,xr,yr,offs,...		selevs,relevs,sdepths,cdps]=fmreadsegy(filename,xopt)% [fmseis,line_name,ntr,nrec,xs,ys,xr,yr,offs,selevs,relevs,...%		sdepths,cdps]=fmreadsegy(filename,xopt)%% FMREADSEGY reads a disk dataset in "Promax" segy format%	and returns a fleximat.%%	filename ... full file name to read from%	xopt ... 0 fleximat x coords are trace number%	         1 fleximat x coords are .5*(xs+xr)%		 2 fleximat x coords are xs%		 3 fleximat x coords are xr%		 val fleximat x coords are val*cdp%   *********** default = 0 **********%%	fmseis ... the fleximat%	line_name ... line name%	ntr ...  number of traces per gather in the segy dataset%	nrec ... number of gathers in the segy dataset%	xs ... inline source coordinates for each trace in fmseis%	ys ... crossline source coordinates for each trace in fmseis%	xr ... inline receiver coordinates for each trace in fmseis%	yr ... crossline receiver coordinates for each trace in fmseis%	offs ... s-r offsets for each trace in fmseis%	selevs ... source elevation for each trace in fmseis%	relevs ... receiver elevation for each trace in fmseis%	sdepths ... source depths for each trace in fmseis%	cdps ... cdp numbers for each trace in fmseis%% NOTE: It is illegal for you to use this software for a purpose other% than non-profit education or research UNLESS you are employed by a CREWES% Project sponsor. By using this software, you are agreeing to the terms% detailed in this software's Matlab source file. % BEGIN TERMS OF USE LICENSE%% This SOFTWARE is maintained by the CREWES Project at the Department% of Geology and Geophysics of the University of Calgary, Calgary,% Alberta, Canada.  The copyright and ownership is jointly held by % its author (identified above) and the CREWES Project.  The CREWES % project may be contacted via email at:  crewesinfo@crewes.org% % The term 'SOFTWARE' refers to the Matlab source code, translations to% any other computer language, or object code%% Terms of use of this SOFTWARE%% 1) Use of this SOFTWARE by any for-profit commercial organization is%    expressly forbidden unless said organization is a CREWES Project%    Sponsor.%% 2) A CREWES Project sponsor may use this SOFTWARE under the terms of the %    CREWES Project Sponsorship agreement.%% 3) A student or employee of a non-profit educational institution may %    use this SOFTWARE subject to the following terms and conditions:%    - this SOFTWARE is for teaching or research purposes only.%    - this SOFTWARE may be distributed to other students or researchers %      provided that these license terms are included.%    - reselling the SOFTWARE, or including it or any portion of it, in any%      software that will be resold is expressly forbidden.%    - transfering the SOFTWARE in any form to a commercial firm or any %      other for-profit organization is expressly forbidden.%% END TERMS OF USE LICENSEif(nargin<2)	xopt=0;end[seis,t,line_name,ntr,nrec,xs,ys,xr,yr,offs,...	selevs,relevs,sdepths,cdps]=readsegy(filename);if(xopt==0)	nx=size(seis,2);	x=1:nx;elseif(xopt==1)	x=.5*(xs+xr);elseif(xopt==2)	x=xs;elseif(xopt==3)	x=xr;else	x=xopt*cdps;endfmseis=fmset(fmseis,x,t,seis);
